describe("classes",function(){var a=document.documentElement.className.split(" ");it("_version exists",function(){expect(Modernizr._version).to.not.be(undefined)});it("_version did not add a class",function(){expect(document.documentElement).to.not.contain("_version")});it("all classes are lower case",function(){expect(document.documentElement.className).to.not.match(/[A-Z]/)});describe("everythings ship shape",function(){_.chain(a).filter().sort().forEach(function(c){var b=c.indexOf("no-")===-1;c=c.replace(/no-/,"");it(c+" is correctly "+b+" in the classes and object",function(){if(_.includes(c,"-")){if(c.replace("-","") in Modernizr){c=c.replace("-","");expect(Modernizr[c]).to.be(b)}else{c=c.split("-");expect(Modernizr[c[0]]).to.not.be(undefined);expect(!!Modernizr[c[0]][c[1]]).to.equal(b)}}else{var e=Modernizr[c];var d=e instanceof Boolean?e.valueOf():!!e;expect(d).to.equal(b)}})}).value()})});